import {
  Search,
  Home,
  Paintbrush,
  Building2,
  KeyRound,
  Handshake,
} from "lucide-react";

const features = [
  {
    title: "Búsqueda de inmuebles a medida",
    desc: "Le damos todo tipo de facilidades para que no tenga que preocuparse de nada a la hora de realizar la búsqueda de inmuebles.",
    icon: Search,
  },
  {
    title: "Venta de inmuebles",
    desc: "Nos encargamos de publicitar ,de seleccionar al inquilino adecuado para los propietarios ",
    icon: Home,
  },
  {
    title: "sERVICIOS DE RELOCATION",
    desc: "Acompañamiento y coordinación integral de obras, reformas y decoración para conseguir espacios a su medida.",
    icon: Paintbrush,
  },
  {
    title: "Gestión de edificios / inmuebles en alquiler",
    desc: "Gestión integral para que su patrimonio esté siempre bien administrado y con la ocupación adecuada.",
    icon: Building2,
  },
  {
    title: "Arrendamiento de inmuebles",
    desc: "Selección de inquilinos, contratos y seguimiento para que el alquiler sea rentable y sin complicaciones.",
    icon: KeyRound,
  },
  {
    title: "Trato con arrendatario",
    desc: "Atención directa y resolución de incidencias para mantener relaciones estables y transparentes.",
    icon: Handshake,
  },
];

export default function WhatWeDo() {
  return (
    <section id="que-hacemos" className="what-section">
      {/* Panel izquierdo oscuro */}
      <div className="what-left">
        <h2>¿QUÉ HACEMOS?</h2>
        <p>
          En NOVA MOVE nos especializamos en ofrecer un servicio integral dentro
          del sector inmobiliario, abarcando todas las áreas que lo componen.
          Gestionamos desde la compra y venta de propiedades, hasta el
          arrendamiento y la búsqueda de inmuebles que se adapten a las
          necesidades de cada cliente. Además, contamos con un servicio de
          relocation para facilitar la llegada y adaptación de particulares y
          empresas, con un proceso ágil, transparente y eficiente. Nuestro
          objetivo es acompañarle en cada paso con soluciones personalizadas y
          de confianza.
        </p>
      </div>

      {/* Lado derecho blanco con rejilla */}
      <div className="what-right">
        <div className="features">
          {features.map(({ title, desc, icon: Icon }) => (
            <div key={title} className="feature">
              <div className="icon">
                <Icon size={20} strokeWidth={2} />
              </div>
              <div>
                <h3>{title}</h3>
                <p>{desc}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
