import React, { useState, useEffect } from 'react';

/**
 * HeroSection component
 * Displays a full‑viewport hero area with a simple fading slider.
 * Each slide contains a title, subtitle and call‑to‑action button.
 */
const HeroSection = () => {
  const slides = [
    {
      title: 'Soluciones inmobiliarias hechas a tu medida',
      description: 'para sus propiedades inmobiliarias',
      buttonLabel: 'Más información',
      link: '#que-hacemos',
      className: 'slide1',
    },
    {
      title: 'Trato directo',
      description: 'personalizado, eficaz y eficiente',
      buttonLabel: 'Más información',
      link: '#servicios',
      className: 'slide2',
    },
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prev) => (prev + 1) % slides.length);
    }, 7000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const current = slides[index];

  return (
    <section className={`hero-section ${current.className}`}>    
      <div className="hero-overlay"></div>
      <div className="container hero-content">
        <h1>{current.title}</h1>
        <p>{current.description}</p>
        <a href={current.link} className="btn btn-primary">
          {current.buttonLabel}
        </a>
      </div>
    </section>
  );
};

export default HeroSection;
