// src/components/HeroSection.js
import React, { useEffect, useState } from 'react';

const HeroSection = () => {
  // Puedes añadir más slides y alternar layout: 'left' (texto izq) | 'right' (texto dcha)
  const slides = [
    {
      layout: 'left',
      title: 'Soluciones inmobiliarias\nhechas a tu medida',
      description: 'para sus propiedades inmobiliarias',
      cta: { label: 'Más información', href: '#que-hacemos' },
      image: '/slides/image1.jpg',
    },
    {
      layout: 'right',
      title: 'Trato directo',
      description: 'personalizado, eficaz y eficiente',
      cta: { label: 'Más información', href: '#servicios' },
      image: '/slides/slide2.jpg',
    },
  ];

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const id = setInterval(() => {
      setIndex((i) => (i + 1) % slides.length);
    }, 7000);
    return () => clearInterval(id);
  }, [slides.length]);

  const current = slides[index];
  const isLeft = current.layout === 'left';

  return (
    <section className="hero-split">
      {/* Panel de texto */}
      <div className={`pane text ${isLeft ? 'left' : 'right'} fade-in`}>
        <div className="text-inner">
          <h1>
            {current.title.split('\n').map((line, i) => (
              <span key={i} className="block">{line}</span>
            ))}
          </h1>
          <p>{current.description}</p>
          {current.cta && (
            <a className="btn btn-primary outline" href={current.cta.href}>
              {current.cta.label}
            </a>
          )}
        </div>
      </div>

      {/* Panel de imagen */}
      <div
        className={`pane image ${isLeft ? 'right' : 'left'} fade-in`}
        style={{ backgroundImage: `url(${current.image})` }}
        aria-label={current.title}
      />
    </section>
  );
};

export default HeroSection;
