import React from 'react';

/**
 * WhatWeDo component
 * Implements the “¿Qué hacemos?” section. On the left a brief
 * description about the company is displayed. On the right a grid
 * of service features with icons summarises the main activities.
 */
const WhatWeDo = () => {
  const features = [
    {
      icon: 'fa-search',
      title: 'Búsqueda de inmuebles a medida',
      description:
        'Le damos todo tipo de facilidades para que no tenga que preocuparse de nada a la hora de realizar la búsqueda de inmuebles',
    },
    {
      icon: 'fa-store',
      title: 'Venta de inmuebles',
      description:
        'Le damos todo tipo de facilidades para que no tenga que preocuparse de nada a la hora de realizar la venta de su propiedad',
    },
    {
      icon: 'fa-paint-roller',
      title: 'Obras, reformas y decoración',
      description:
        'Le ofrecemos acompañamiento y coordinación en obras, reformas y decoración para conseguir espacios a su medida',
    },
    {
      icon: 'fa-building',
      title: 'Gestión de edificios y alquiler',
      description:
        'Nos encargamos de la gestión integral de edificios y alquileres para que su patrimonio esté siempre bien administrado',
    },
  ];

  return (
    <section id="que-hacemos" className="what-we-do">
      <div className="container what-content">
        <div className="what-left">
          <h2>¿Qué hacemos?</h2>
          <p>
            En Property Plus nos dedicamos a la gestión inmobiliaria englobando
            todos los campos que ésta comprende, desde la venta al arrendamiento
            pasando por la búsqueda de soluciones a medida y la obra y reforma.
          </p>
          <p>
            Durante los últimos años hemos demostrado nuestra capacidad para
            encontrar oportunidades y proporcionar un servicio de calidad en
            circunstancias complejas. Nuestro equipo acompaña a cada cliente en
            todo el proceso de compraventa y alquiler con una actitud cercana y
            profesional.
          </p>
        </div>
        <div className="what-right">
          {features.map((feat, idx) => (
            <div className="feature" key={idx}>
              <div className="feature-icon">
                <i className={`fas ${feat.icon}`}></i>
              </div>
              <div>
                <h3>{feat.title}</h3>
                <p>{feat.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default WhatWeDo;
